import React, { Component } from "react";
import { connect } from "react-redux";
import { faqActions } from "../../actions";
import {
  Container,
  Accordion,
  Label,
  Loader,
  Segment,
  Dimmer,
  Image,
  Header,
  Icon,
  Grid,
} from "semantic-ui-react";

import { AccordionFAQ } from "./AccordionFAQ";

class FAQ extends Component {
  async componentDidMount() {
    await this.props.loadAllFAQ();
  }

  render() {
    const { faqs, loaded } = this.props;
    return (
      <Container>
        <Label color='blue' size='massive' ribbon>
          FAQs
        </Label>
        <Grid>
          <Grid.Row centered></Grid.Row>
          <Grid.Row style={{ marginTop: "1.5%", marginLeft: "0.5%" }}>
            <Accordion fluid styled>
              {loaded === false ? (
                <Segment>
                  <Dimmer active inverted>
                    <Loader inverted content='Loading' />
                  </Dimmer>

                  <Image src='/images/wireframe/short-paragraph.png' />
                </Segment>
              ) : faqs && faqs.length === 0 ? (
                <Header
                  style={{ marginTop: "5%", marginLeft: "32%" }}
                  as='h2'
                  icon
                >
                  <Icon name='question' />
                  No FAQs
                  <Header.Subheader>
                    Looks like there are no frequently asked questions..
                  </Header.Subheader>
                </Header>
              ) : (
                faqs &&
                faqs.map((faq) => (
                  <AccordionFAQ
                    key={faq._id}
                    id={faq._id}
                    question={faq.question}
                    answer={faq.answer}
                  />
                ))
              )}
            </Accordion>
          </Grid.Row>
        </Grid>
      </Container>
    );
  }
}

const mapState = (state) => {
   const { faqs, loaded } = state.faqs;
  return { faqs, loaded };
}
const actionCreators = {
  loadAllFAQ: faqActions.loadAllFAQ,
};

const connectedFAQ = connect(mapState, actionCreators)(FAQ);

export { connectedFAQ as FAQ };
