import React, { Component } from "react";
import { connect } from "react-redux";
import { moneyWithdrawalActions } from "../../actions";
import { Header, Table, Container, Button } from "semantic-ui-react";
import * as moment from "moment";

class MoneyWithdrawal extends Component {
  componentDidMount() {
    this.props.getAllRequests();
  }
  formatDate(date) {
    return moment(date).format("YYYY-MM-DD HH:mm:ss");
  }
  removeItem = (e, { id }) => {
    this.props.removeItem(id);
  };

  disableButton(status) {
    return status === "canceled";
  }
  routeChange = (e, { id }) => {
    this.props.history.push(`${`/withdrawal/edit/${id}`}`);
  };

  render() {
    const { withdrawalRequests } = this.props;

    const renderMoneyWithdrawal = withdrawalRequests.length ? (
      withdrawalRequests.map((request, i) => {
        let address = "";
        let fullAddress = "";
        if (request.SP) {
          address = request.SP.address;
          fullAddress = `${address.building},${address.street},${address.district},${address.city}`;
        }

        const id = request._id;
        return (
          <Table.Row key={i}>
            <Table.Cell>
              <Header as="h4" image>
                <Header.Content>{request.status}</Header.Content>
              </Header>
            </Table.Cell>
            <Table.Cell>{request.amount}</Table.Cell>
            <Table.Cell>{this.formatDate(request.timeslot)}</Table.Cell>
            <Table.Cell>{fullAddress}</Table.Cell>
            <Table.Cell>
              <Button
                className=" fluid negative ui button"
                negative
                disabled={this.disableButton(request.status)}
                onClick={this.removeItem}
                id={id}
              >
                Cancel
              </Button>

              <Button
                positive
                className=" fluid  ui button"
                disabled={this.disableButton(request.status)}
                onClick={this.routeChange}
                id={id}
              >
                Edit
              </Button>
            </Table.Cell>
          </Table.Row>
        );
      })
    ) : (
      <Table.Row key={1}></Table.Row>
    );
    return (
      <Container className="container">
        <Table align="center" color={"red"}>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Status</Table.HeaderCell>
              <Table.HeaderCell>Amount</Table.HeaderCell>
              <Table.HeaderCell>Time Slot</Table.HeaderCell>
              <Table.HeaderCell>Service Point</Table.HeaderCell>
              <Table.HeaderCell></Table.HeaderCell>
              <Table.HeaderCell></Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>{renderMoneyWithdrawal}</Table.Body>
        </Table>
      </Container>
    );
  }
}
function mapState(state) {
  const { user } = state.authentication;
  const { withdrawalRequests } = state.moneyWithdrawal;
  return {
    user,
    withdrawalRequests,
  };
}
const actionCreators = {
  getAllRequests: moneyWithdrawalActions.getAllRequest,
  removeItem: moneyWithdrawalActions.removeItem,
};
const connectedMoneyWithdrawal = connect(
  mapState,
  actionCreators
)(MoneyWithdrawal);

export { connectedMoneyWithdrawal as moneyWithdrawal };
