import React, { Component } from "react";
import { connect } from "react-redux";
import { Segment, Button, Icon, Input } from "semantic-ui-react";
import { userActions } from "../../actions/user.actions";
import "./UserProfilePage.css";

class inviteFriends extends Component {
  state = {
    mobile: "",
    isMobileValid: false,
    enableSubmit: false,
  };

  mobileChanged = (e, data, value) => {
    const isMobileValid = /^01[0-2|5]{1}[0-9]{8}$/.test(data.value);
    this.setState({
      mobile: data.value,
      isMobileValid: isMobileValid,
      enableSubmit: isMobileValid,
    });

  };

  render() {
    return (
      <Segment textAlign="center"  className="inviteFriendsContainer">
        <div className="ui focus input"> 
          <Input type="text"    onChange={this.mobileChanged} />
          {this.state.isCodeValid ? (
            <Icon color="green" name="checkmark" size="small" />
          ) : (
            <Icon color="yellow" name="warning" size="small" />
          )}
          <Button.Group>
            <Button onClick={this.close}>Cancel</Button>
            <Button.Or />
            {this.state.enableSubmit ? (
              <Button
                positive
                onClick={() => {
                  this.props.handlesendInvitationByPhoneNumber(
                    this.state.mobile
                  );
                }}
              >
                Save
              </Button>
            ) : (
              <Button positive disabled>
                Save
              </Button>
            )}
          </Button.Group>
        </div>
      </Segment>
    );
  }
}

function mapState(state) {
  const { invitationsByNumbers } = state.users;
  return { invitationsByNumbers };
}

const actionCreators = {
  handlesendInvitationByPhoneNumber: userActions.sendInvitationByPhoneNumber,
};

const connectedInviteFriends = connect(mapState, actionCreators)(inviteFriends);

export { connectedInviteFriends as inviteFriends };
