import React from 'react';
import { Label } from 'semantic-ui-react';
import { onSale } from '../../helpers';

const DiscountLabel = ({ post }) => {
    const { discount } = post;

    if (!onSale(discount)) return <React.Fragment></React.Fragment>;
    const { discount_value, discount_unit } = discount;
    return (
        <React.Fragment>
            {
                <Label color='red' ribbon>
                    {discount_value} {discount_unit === 'percentage' ? '%' : 'LE'} OFF
                </Label>
            }
        </React.Fragment>
    );
}

export default DiscountLabel;
