import React, { Component } from "react";
import { Container, Image } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import { imageUrl } from "./config";
import DesktopBreakpoint from "../../components/Responsive_utillities/desktop_breakPoint";
import PhoneBreakpoint from "../../components/Responsive_utillities/phone_breakPoint";
import TabletMainSection from "./TabletMainSection";
class header extends Component {
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container>
        <DesktopBreakpoint name="desktop">
          <TabletMainSection/>
        </DesktopBreakpoint>

        <PhoneBreakpoint name="phone">
          <div className={`headerSection ${language}`}>
            <Image
              src={
                language === "ar"
                  ? `${imageUrl}/landing-page/VarietyAR@3x-min.png`
                  : `${imageUrl}/landing-page/iphone-12-mockup-floating-shadow@3x-min.png`
              }
              alt="landingPage header image"
              loading="lazy"
            />
            <div className={`headerSectionDescription ${language}`}>
              <span>{t("landingPage:landingPageHeaderTitle")}</span>
              <p>{t("landingPage:landingPageHeaderMainTitle")}</p>
              <div>
                {t("landingPage:landingPageHeaderDescription")}
                <br />
                <br />
                {t("landingPage:landingPageHeaderDescription2")}
              </div>
            </div>
          </div>
        </PhoneBreakpoint>
      </Container>
    );
  }
}

export default withTranslation("landingPage")(header);
