import { watchListConstants } from "../constants";

const initialState = {
  watchListData: []
};
export function watchList(state = initialState, action) {
  switch (action.type) {
    case watchListConstants.ADD_WATCHLIST_REQUEST:
      return {
        ...state
      };
    case watchListConstants.ADD_WATCHLIST_SUCCESS:

      return {
        ...state,
        watchListData: [...state.watchListData, action?.post]
      };
    case watchListConstants.ADD_WATCHLIST_FAILURE:

      return {
        ...state
      };

    case watchListConstants.VIEW_WATCHLIST_REQUEST:
      return {
        ...state
      };
    case watchListConstants.VIEW_WATCHLIST_SUCCESS:
      return {
        ...state,
        watchListData: action.watchListData
      };
    case watchListConstants.VIEW_WATCHLIST_FAILURE:
      return {
        ...state
      };
    case watchListConstants.REMOVE_WATCHLIST_REQUEST:
      return {
        ...state
      };
    case watchListConstants.REMOVE_WATCHLIST_SUCCESS:
      let updatedState = {
        ...state
      };
      const filteredPosts = updatedState.watchListData.filter(function(item) {
        return item._id !== action.postId;
      });
      updatedState.watchListData = filteredPosts;
      return updatedState;
    case watchListConstants.REMOVE_WATCHLIST_FAILURE:
      return {
        ...state
      };
    default:
      return {
        ...state
      };
  }
}
