import { contactUsService } from "../services";
import { alertActions } from "./";
import {contactUsConstants} from '../constants';


export const contactUsActions = {
    contactUsForm
};




function contactUsForm(contactUsObj) {
    return async (dispatch) => {
      //TODO Need some backend validation change
      /*
      await dispatch(request(contactUsObj));
      try {
        await contactUsService.contactUsForm(contactUsObj);
        dispatch(success(contactUsObj));
      } catch (ex) {
        dispatch(failure(ex));
        dispatch(alertActions.error({header: ex.message}));
      }*/
    };
  
    function request(contactUsObj) {
      return {type: contactUsConstants.CONTACT_US_FORM_REQUEST, contactUsObj};
    }
  
    function success(contactUsObj) {
      return {type: contactUsConstants.CONTACT_US_FORM_SUCCESS, contactUsObj};
    }
  
    function failure(errors) {
      return {type: contactUsConstants.CONTACT_US_FORM_FAILURE, errors};
    }
  
  }
