import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Grid, Label } from 'semantic-ui-react';
class OrderInfoPage extends Component {
    state = {};
    render() {
        const orders = this.props.ordersData;
        return <Grid>
            <Grid.Row><Label size='huge' color='green'>Your order has been submitted successfully !</Label></Grid.Row>
            <Grid.Row><Label>Orders Numbers : </Label></Grid.Row>
            {orders.map(orderData => getOrderAsRow(orderData))}
        </Grid>;
    }
}

function getOrderAsRow(orderData) {
    return (<Grid.Row key={orderData._id}><Label>{orderData._id} </Label></Grid.Row>);
}

function mapState(state) {
    const { user } = state.authentication;
    const { ordersData } = state.order;
    return {
        user,
        ordersData
    };
}

const actionCreators = {};

const connectedOrderInfoPage = connect(mapState, actionCreators)(OrderInfoPage);

export { connectedOrderInfoPage as OrderInfoPage };
