import { httpService } from './';
import { apiUrl } from '../config';

const apiEndpoint = apiUrl + '/invitation';

export const invitationService = { sendInvitation, viewAllMyInvitations, resendInvitation, sendInvitationByPhoneNumber, keepInTouchRequest };

function sendInvitationByPhoneNumber(invitation) {
    return httpService.post(`${apiUrl}/phoneInvitation`, invitation);
}


function sendInvitation(invitation) {
    return httpService.post(apiEndpoint, invitation);
}

function resendInvitation(invitationId) {
    return httpService.put(`${apiEndpoint}/resend/${invitationId}`);
}

function viewAllMyInvitations() {
    return httpService.get(apiEndpoint);
}

function keepInTouchRequest(formObj) {
    return httpService.post(`${apiUrl}/keepInTouch`, formObj);
}
