import React, { Component } from 'react';
import { connect } from 'react-redux';
import _ from 'lodash';
import { Segment, Tab, Label, Icon, Rating, Table, } from 'semantic-ui-react';

class Reviews extends Component {
    state = { reviewsAsSeller: [], reviewsAsBuyer: [], reviewsForOthers: [], column: null, data: [], direction: null}

    async componentDidMount() {
        this.setState({
            reviewsAsSeller: this.props.userPublicDetails.reviewsAsSeller,
            reviewsAsBuyer: this.props.userPublicDetails.reviewsAsBuyer,
            reviewsForOthers: this.props.userPublicDetails.reviewsForOthers,
        })
    }

    
    handleSort = (clickedColumn, type) => () => {
        const { reviewsAsBuyer, reviewsAsSeller, reviewsForOthers, column, direction } = this.state
    
        if (column !== clickedColumn) {
            switch (type) {
                case 'buyer':
                    this.setState({ column: clickedColumn, reviewsAsBuyer: _.sortBy(reviewsAsBuyer, [clickedColumn]), direction: 'ascending' });            
                    break;
                case 'seller':
                    this.setState({ column: clickedColumn, reviewsAsSeller: _.sortBy(reviewsAsSeller, [clickedColumn]), direction: 'ascending' });            
                    break;
                default:
                    this.setState({ column: clickedColumn, reviewsForOthers: _.sortBy(reviewsForOthers, [clickedColumn]), direction: 'ascending' });            
                    break;
            }
          return;
        }

        switch (type) {
            case 'buyer':
                this.setState({ reviewsAsBuyer: reviewsAsBuyer.reverse(), direction: direction === 'ascending' ? 'descending' : 'ascending' });
                break;
            case 'seller':
                this.setState({ reviewsAsSeller: reviewsAsSeller.reverse(), direction: direction === 'ascending' ? 'descending' : 'ascending' });
                break;
            default:
                this.setState({ reviewsForOthers: reviewsForOthers.reverse(), direction: direction === 'ascending' ? 'descending' : 'ascending' });
                break;
        }
    }
    
    getStarsRating = (number) => <Rating icon='star' defaultRating={number} maxRating={5} size='large' disabled/>;

    renderTable = (reviews, type) => {
        const { column, direction } = this.state
        return (
        <Table sortable celled padded>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell sorted={column === 'feedback' ? direction : null} onClick={this.handleSort('feedback', type)}>Feedback</Table.HeaderCell>
              <Table.HeaderCell>{type === 'left' ? 'To' : 'From'}</Table.HeaderCell>
              <Table.HeaderCell sorted={column === 'date' ? direction : null} onClick={this.handleSort('updatedAt', type)}>Date</Table.HeaderCell>
              {type !== 'buyer' && <Table.HeaderCell sorted={column === 'feedback' ? direction : null} onClick={this.handleSort('sellerRating.postDescription', type)}>Post Description</Table.HeaderCell>}
              {type !== 'buyer' && <Table.HeaderCell sorted={column === 'feedback' ? direction : null} onClick={this.handleSort('sellerRating.communication', type)}>Communication</Table.HeaderCell>}
              <Table.HeaderCell>Comments</Table.HeaderCell>
            </Table.Row>
          </Table.Header>
      
          <Table.Body>
                {reviews.map((review) => (
                    <Table.Row key={review._id}>
                        <Table.Cell textAlign='center'>{ review.feedback === 1 ? <Icon name='thumbs up outline' color='green'/> : <Icon name='thumbs down outline' color='red'/> }</Table.Cell>
                        <Table.Cell singleLine>
                            ***{type === 'left' ? review.reviewee.slice(-3) : review.reviewer.slice(-3)}
                        </Table.Cell>
                        <Table.Cell>
                            {new Date(review.updatedAt).toLocaleDateString()}
                            <br />
                            {new Date(review.updatedAt).toLocaleTimeString()}
                        </Table.Cell>
                        {type !== 'buyer' && <Table.Cell>{review.revieweeType === 'seller' && <Rating icon='star' defaultRating={review.sellerRating.postDescription} maxRating={5} disabled/>}</Table.Cell>}
                        {type !== 'buyer' && <Table.Cell>{review.revieweeType === 'seller' && <Rating icon='star' defaultRating={review.sellerRating.communication} maxRating={5} disabled/>}</Table.Cell>}
                        <Table.Cell textAlign='left'>{review.text || '---'}</Table.Cell>
                    </Table.Row>
                ))}
          </Table.Body>
        </Table>
    )}

    render() {
        const { reviewsAsSeller, reviewsAsBuyer, reviewsForOthers } = this.state;
      
        const panes = [
            {
                menuItem: 'As Seller',
                render: () => (
                    <Tab.Pane attached='top'>
                        {reviewsAsSeller && reviewsAsSeller.length > 0 ? (
                            this.renderTable(reviewsAsSeller, 'seller')
                        ) : (
                            <Label>No Reviews Yet</Label>
                        )}
                    </Tab.Pane>
                ),
            },
            {
                menuItem: 'As Buyer',
                render: () => (
                    <Tab.Pane attached='top'>
                        {reviewsAsBuyer && reviewsAsBuyer.length > 0 ? (
                            this.renderTable(reviewsAsBuyer, 'buyer')
                        ) : (
                            <Label>No Reviews Yet</Label>
                        )}
                    </Tab.Pane>
                ),
            },
            {
                menuItem: 'For Others',
                render: () => (
                    <Tab.Pane attached='top'>
                        {reviewsForOthers && reviewsForOthers.length > 0 ? (
                            this.renderTable(reviewsForOthers, 'left')
                        ) : (
                            <Label>No Reviews Yet</Label>
                        )}
                    </Tab.Pane>
                ),
            },
        ];

        return (
            <Segment>
                <Tab
                    menu={{ attached: 'top' }}
                    panes={panes}
                ></Tab>
            </Segment>
        );
    }
}

function mapState(state) {
    const { user } = state.authentication;
    const { userPublicDetails } = state.users;
    return { userPublicDetails, user };
}

const connectedReviews = connect(mapState)(Reviews);

export { connectedReviews as Reviews };
