import { voucherConstants } from '../constants';

const initialState = {
    voucher: {},
    error: '',
    loading: false,
}

export function voucher(state = initialState, action) {
    switch (action.type) {
        case voucherConstants.VALIDATE_VOUCHER_CODE_REQUEST:
            return { ...state, loading: true };
        case voucherConstants.VALIDATE_VOUCHER_CODE_SUCCESS:
            return { error: '', voucher: action.voucher, loading: false };
        case voucherConstants.VALIDATE_VOUCHER_CODE_FAILURE:
            return { voucher: {}, error: action.error.message, loading: false };
    
            default:
            return { ...state };
    }
}
