import React, { Component } from "react";
import { Segment, Image, Button, Container } from "semantic-ui-react";
import { connect } from "react-redux";
import { withTranslation } from "react-i18next";
import { userActions } from "../../actions";
import { imageUrl } from "./config";
import "./LandingPageHeader.css";
import DesktopBreakpoint from "../../components/Responsive_utillities/desktop_breakPoint";
import PhoneBreakpoint from "../../components/Responsive_utillities/phone_breakPoint";
class NavBar extends Component {
  changeLanguage(language) {
    this.props.changeLanguage(language === "en" ? "ar" : "en");
  }

  render(props) {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    console.log("navbarMobileButton",language,t("navbarMobileButton"))
    return (
      <Container>
        <DesktopBreakpoint name="desktop">
          <Segment className={`navbarContainer ${language}`}>
            <Image
              src={
                language === "ar"
                  ? `${imageUrl}/landing-page/ar-header-logo@3x-min.png`
                  : `${imageUrl}/landing-page/Header-Logo-min.png`
              }
              className={`Navbarlogo ${language}`}
              alt="Navbarlogo"
            />
            <Button
              className={`navbarMobileButton ${language}`}
              onClick={() => this.changeLanguage(this.props.language)}
            >
              {t("otherlang")}
            </Button>
            {this.props.children}
          </Segment>
        </DesktopBreakpoint>

        <PhoneBreakpoint name="phone">
          <Segment className={`navbarContainer ${language}`}>
            <Image
              src={
                language === "ar"
                  ? `${imageUrl}/landing-page/ar-header-logo@3x-min.png`
                  : `${imageUrl}/landing-page/Logo@3x-min.png`
              }
              className={`Navbarlogo ${language}`}
              alt="Navbarlogo"
            />
            <Button
              className={`navbarMobileButton ${language}`}
              onClick={() => this.changeLanguage(this.props.language)}
            >
              {t("otherlang")}
            </Button>
            {this.props.children}
          </Segment>
        </PhoneBreakpoint>
      </Container>
    );
  }
}
function mapState(state) {
  const { language } = state.users;
  return { language };
}

const actionCreators = {
  changeLanguage: userActions.setLanguage,
};
const connectedNavBar = connect(
  mapState,
  actionCreators
)(withTranslation()(NavBar));
export { connectedNavBar as NavBar };
