import React, { Component } from "react";
import { Container, Image, Segment } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import { imageUrl } from "./config";
class Variety extends Component {
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container>
        <Segment className={`generalSection ${language}`}>
          <Image
            src={`${imageUrl}/landing-page/background@3x-min%20(1).png`}
            className="backgroundImg"
            loading="lazy"
          />
          <Image
             src={
              language === "ar"
                ? `${imageUrl}/landing-page/VarietyAR@3x-min.png`
                : `${imageUrl}/landing-page/iphone-12-mockup-floating-shadow@3x-min.png`
            }
    
            className="varietyImg"
            loading="lazy"
          />
        </Segment>
        <Segment className={`VarietySection ${language}`}>
          <h4> {t("landingPage:varietyMainTitle")} </h4>
          <span> {t("landingPage:varietySubTitle")} </span>
          <p>{t("landingPage:varietyPragraph")}</p>
        </Segment>
      </Container>
    );
  }
}
export default withTranslation("landingPage")(Variety);
