import { httpService } from "./";
import { apiUrl } from "../config";

export const avatarService = {
    getActiveAvatar,
    setActiveAvatar,
};

const apiEndpoint = apiUrl + "/users";

function getActiveAvatar() {
  return httpService.get(`${apiEndpoint}/avatar/getAvatar`);
}
function setActiveAvatar(avatarUrl) {
    return httpService.put(`${apiEndpoint}/avatar/updateAvatar`, {
      avatar: avatarUrl,
    });
}
