import { biddingHistoryConstants } from '../constants';
import { biddingHistory } from './../services';
import { alertActions } from './';
export const biddingHistoryActions = {
    viewBiddingHistory
}
function viewBiddingHistory(auctionId) {
    return async (dispatch) => {
        await dispatch(request());
        try {
            const  {data}  = await biddingHistory.getBiddingHistory(auctionId);            
            dispatch(success(data));
        } catch (ex) {
            dispatch(failure(ex));
            dispatch(
                alertActions.error({
                    header: "can't view cart details",
                    content: ex.message,
                }),
            );
        }
    };

    function request() {
        return {
            type: biddingHistoryConstants.VIEW_BIDDING_HISTORY_REQUEST,
        };
    }

    function success(data) {
        return {
            type: biddingHistoryConstants.VIEW_BIDDING_HISTORY_SUCCESS,
            data,
        };
    }

    function failure(error) {
        return {
            type: biddingHistoryConstants.VIEW_BIDDING_HISTORY_FAILURE,
            error,
        };
    }
}
