import { keepInTouchConstant } from "../constants";
const initialState = {
  keepInTouchState: {},
};
export function keepInTouch(state = initialState, action) {
  switch (action.type) {
    case keepInTouchConstant.KEEP_IN_TOUCH_REQUEST:
      return {
        ...state,
        keepInTouchState: {},
      };
    case keepInTouchConstant.KEEP_IN_TOUCH_SUCCESS:
      return {
        ...state,
        keepInTouchState: { ...action.data, success: true },
      };
    case keepInTouchConstant.KEEP_IN_TOUCH_FAILURE:
      return {
        ...state,
        keepInTouchState: { success: false, error: action.error },
      };
    default:
      return state;
  }
}
