import { httpService } from "./";
import { apiUrl } from "../config";

export const orderService = {
  viewBuyingOrders,
  viewSellingOrders,
  viewSellingPosts,
  placeOrder,
  placeReturnRequest,
  viewAllOrders,
  viewOrder,
  updateSellerDropOffInfo,
  updateBuyerPickUpInfo,
  updateSellerAuthorizedDropOffInfo,
  updateBuyerAuthorizedPickUpInfo,
  submitBuyerPickupInfo,
  editOrder,
  chooseBuyNow,
  chooseAuctionWithMinPrice,
  chooseAuctionWithoutMinPrice,
};

const apiEndpoint = apiUrl + "/order";
const servicePointApiEndpoint = apiUrl + "/servicePoint";
const returnRequestEndPoint = apiUrl + "/returnRequest";


function viewBuyingOrders(){
  return httpService.get(apiEndpoint + "/ordersAsBuyer");
}

function viewSellingOrders(){
  return httpService.get(apiEndpoint + "/ordersAsSeller");
}

function viewSellingPosts(){
  return httpService.get(`${apiUrl}/post/myPosts`);
}

function placeOrder(orderData) {
  return httpService.post(apiEndpoint, orderData);
}

function viewAllOrders() {
  return httpService.get(apiEndpoint);
}

function viewOrder(orderId) {
  return httpService.get(apiEndpoint + "/" + orderId);
}

function updateSellerDropOffInfo(orderId, { SP, timeslot }) {
  return httpService.put(servicePointApiEndpoint + "/order/seller/" + orderId, {
    SP,
    timeslot,
  });
}

function updateSellerAuthorizedDropOffInfo(orderId, { SP, timeslot }) {
  return httpService.put(
    servicePointApiEndpoint + "/order/seller-authorized/" + orderId,
    { SP, timeslot }
  );
}

function updateBuyerPickUpInfo(orderId, { SP, timeslot }) {
  return httpService.put(servicePointApiEndpoint + "/order/buyer/" + orderId, {
    SP,
    timeslot,
  });
}

function updateBuyerAuthorizedPickUpInfo(orderId, { SP, timeslot }) {
  return httpService.put(
    servicePointApiEndpoint + "/order/buyer-authorized/" + orderId,
    { SP, timeslot }
  );
}

function placeReturnRequest(orderId, itemId, reason) {
  return httpService.post(
    returnRequestEndPoint + "/" + orderId + "/" + itemId,
    { reason }
  );
}

function submitBuyerPickupInfo(placeOrderRequest, orderId) {
  return httpService.put(`${apiEndpoint}/submitBuyerPickupInfo/${orderId}`, placeOrderRequest);
}

function editOrder(orderId, orderData) {
  return httpService.put(`${apiEndpoint}/${orderId}`, orderData);
}

function chooseBuyNow(orderId,body){
  return httpService.put(`${apiUrl}/item/chooseBuyNow/${orderId}`,body)
}
function chooseAuctionWithMinPrice(orderId,body){
  return httpService.put(`${apiUrl}/item/chooseAuctionWithMinPrice/${orderId}`,body)
}
function chooseAuctionWithoutMinPrice(orderId,body){
  return httpService.put(`${apiUrl}/item/chooseAuctionWithoutMinPrice/${orderId}`,body)
}
