import {httpService} from "./";
import {apiUrl} from "../config";

export const categoryService = {
  getAll,
  getById,
  getDetails,
  getFields,
  viewFavoriteCategories,
  unFavoriteCategory,
  favoriteCategory
};

const apiEndpoint = apiUrl + "/category";

function getAll() {
  return httpService.get(apiEndpoint);
}

function getById(id) {

}

export function getDetails(id) {
  return httpService.get(`${apiEndpoint}/${id}`);
}

export function getFields(id) {
  return httpService.get(`${apiEndpoint}/fields/${id}`);
}

function viewFavoriteCategories() {
  return httpService.get(`${apiEndpoint}/favorites`);
}

function unFavoriteCategory(categoryId) {
  return httpService.put(`${apiEndpoint}/unfavorite/${categoryId}`);
}

function favoriteCategory(categoryId) {
  return httpService.put(`${apiEndpoint}/favorite/${categoryId}`);
}
