import {postConstants} from '../constants';

const initialState = {
  loading: false,
  posts: [],
};

export function myPosts(state = initialState, action) {
  switch (action.type) {
    case postConstants.GET_MY_POSTS_REQUEST:
      return {...state, loading: true};
    case postConstants.GET_MY_POSTS_SUCCESS:
      return {...state, posts: action.posts, loading: false};
    case postConstants.GET_MY_POSTS_FAILURE:
      return {...state, errors: action.errors, loading: false};

    case postConstants.DELETE_POST_REQUEST:
      return {...state, loading: true};
    case postConstants.DELETE_POST_SUCCESS:
      return {...state, posts: action.posts, loading: false};
    case postConstants.DELETE_POST_FAILURE:
      return {...state, errors: action.errors, loading: false};      

    case postConstants.CLEAR:
      return initialState;
    default:
      return state;
  }
}
