import React, { Component } from "react";
import { Segment, Container, Button, Grid } from "semantic-ui-react";
import DesktopBreakpoint from "../../components/Responsive_utillities/desktop_breakPoint";
import PhoneBreakpoint from "../../components/Responsive_utillities/phone_breakPoint";
import { withTranslation } from "react-i18next";

class HowIsWorks extends Component {
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container>
        <DesktopBreakpoint name="desktop">
          <Segment className={`tablethowitworks ${language}`}>
            <div className="howMazadatWorksSection">
              <Grid columns={2} padded>
                <Grid.Column className={`howItWorksSectionTitle ${language}`}>
                  <span>{t("landingPage:howMazadatWorksTablet")}</span>
                  <div>{t("landingPage:howMazadatWorkstitleTablet")}</div>
                </Grid.Column>
                <Grid.Column>
                  <Segment className={`Watchthevideo ${language}`}>
                    <Button>
                      <span>{t("landingPage:videoButton")}</span>
                    </Button>
                  </Segment>
                </Grid.Column>
              </Grid>
            </div>
          </Segment>
        </DesktopBreakpoint>

        <PhoneBreakpoint name="phone">
          <Segment className={`howitworks ${language}`}>
            <div>
              <p>
                {t("landingPage:howMazadatWorks")}

                <span> {t("landingPage:mazadat")} </span>

                {t("landingPage:forYou")}
              </p>
            </div>
            <Button>
              <span>{t("landingPage:videoButton")}</span>
            </Button>
          </Segment>
          <Segment className={`mazadatbenefits ${language}`}>
            <span className="mazadatbenefitsspan">
              {t("landingPage:howItWorks")}
            </span>

            <p>{t("landingPage:theBetafits")}</p>

            <span className="learnAboutmazadatbenefits">
              {t("landingPage:mazadatBenefits")}
            </span>
          </Segment>
        </PhoneBreakpoint>
      </Container>
    );
  }
}
export default withTranslation("landingPage")(HowIsWorks);
