import React, { Component } from "react";
import { Header, Image, Table, Container } from "semantic-ui-react";
import * as moment from 'moment';
import { connect } from "react-redux";
import { biddingHistoryActions} from "../../actions";
class BiddingHistory extends Component {
  
  componentDidMount() {
    const {id}=this.props.match.params
    this.props.getBiddingHistory(id)
  }
  formatDate(date){
    return  moment(date).format("YYYY-MM-DD HH:mm:ss");
  }
  render() {
    
    const {biddingHistory} = this.props
    const bidHistory = biddingHistory.length ? (
      biddingHistory.map((bid ,i)=> {
        return (
          <Table.Row key={i}>
            <Table.Cell>
              <Header as="h4" image>
                <Image
                  src="https://react.semantic-ui.com/images/avatar/small/lena.png"
                  rounded
                  size="mini"
                />
                <Header.Content>
                  {bid.user.name}
                  <Header.Subheader>{bid.type}</Header.Subheader>
                </Header.Content>
              </Header>
            </Table.Cell>
            <Table.Cell>{bid.amount}</Table.Cell>
            <Table.Cell>{this.formatDate(bid.createdAt)}</Table.Cell>
          </Table.Row>
        );
      })
    ):(
      
      <Table.Row key={1}>
        </Table.Row>
       );
    return (
      <Container className="container">
        <Table align="center" color={"red"}>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell>Bidder</Table.HeaderCell>
              <Table.HeaderCell>Bid Amount</Table.HeaderCell>
              <Table.HeaderCell>Date</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

    <Table.Body>{bidHistory}</Table.Body>
        </Table>
      </Container>
    );
  }
}

function mapState(state) {
  const { user } = state.authentication;
  const { biddingHistory } = state.biddingHistory;
  return {
    user,
    biddingHistory
  };
}
const actionCreators = {
  getBiddingHistory: biddingHistoryActions.viewBiddingHistory
};
const connectedBiddingHistory = connect(mapState, actionCreators)(BiddingHistory);
export { connectedBiddingHistory as biddingHistory };
