import React, { Component } from 'react';
import { connect } from 'react-redux';
import { userActions } from '../../actions';
import { Header, Segment, Icon, Grid, Menu } from 'semantic-ui-react';
import { FeedBack } from './Feedback';
import { Reviews } from './Reviews';
import { history } from '../../helpers';
import { routeConstants } from '../../constants';

class UserPage extends Component {
    state={}

    componentDidMount() {
        if (this.props.user.id === this.props.match.params.id) history.push(routeConstants.PROFILE);
        this.props.getUserPublicDetails(this.props.match.params.id);
    }

    handleItemClick = (e, { name }) => this.setState({ activeItem: name });

    render() {
        const { userPublicDetails } = this.props;
        const { activeItem } = this.state;

        return (
            <Segment textAlign='left'>
                {
                    userPublicDetails && userPublicDetails.user &&
                    <div>
                        <Header textAlign='center' as='h2' icon>
                            <Icon name='user' />
                            {`${userPublicDetails.user.username}'s Profile`}
                            <Header.Subheader>
                                Profile Details
                            </Header.Subheader>
                        </Header>
                        <Grid textAlign='center'>
                            <Grid.Row>
                                <Grid.Column width={2}>
                                    <Menu fluid vertical>
                                        <Menu.Item
                                            name='FEEDBACK'
                                            active={activeItem === 'FEEDBACK'}
                                            onClick={ this.handleItemClick }
                                        >
                                            Feedback
                                        </Menu.Item>
                                        <Menu.Item
                                            name='REVIEWS'
                                            active={activeItem === 'REVIEWS'}
                                            onClick={ this.handleItemClick }
                                        >
                                            Reviews
                                        </Menu.Item>
                                    </Menu>
                                </Grid.Column>
                                <Grid.Column width={12}>
                                    { activeItem === 'REVIEWS' ? <Reviews/> : <FeedBack /> }
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </div>
                }
            </Segment>
        );
    }
}

function mapState(state) {
    const { user } = state.authentication;
    const { userPublicDetails } = state.users;
    return { user, userPublicDetails };
}

const actionCreators = {
    getUserPublicDetails: userActions.viewUserPublicDetails,
};

const connectedUserPage = connect(mapState, actionCreators)(UserPage);
export { connectedUserPage as UserPage };
