import React, { Component } from "react";
import { connect } from "react-redux";
import { Form, Label, Input, Button } from "semantic-ui-react";
import { servicePointActions } from "../../actions";
import { SelectSPAndTimeSlot } from "./SelectSPAndTimeSlot";

class AuthorizedPersonForm extends Component {
  state = {};
  authorizedNameChanged = (e, { value }) => {
    this.props.authorizedPersonNameChanged(value);
  };
  authorizedIDChanged = (e, { value }) => {
    this.props.authorizedPersonIDChanged(value);
  };
  confirmAuthorizedInfo = () => {
    this.props.confirmAuthorizedPersonInfo(this.props.authorizedID);
  };
  render() {
    const {
      authorizedDataConfirmed,
      authorizedName,
      authorizedID,
    } = this.props;
    return (
      <Form>
        <Form.Field>
          <Label>PLease enter name of authorized person :</Label>
          <Input
            onChange={this.authorizedNameChanged}
            value={authorizedName}
            disabled={authorizedDataConfirmed}
          ></Input>
        </Form.Field>

        <Form.Field>
          <Label>PLease enter national ID of authorized person :</Label>
          <Input
            onChange={this.authorizedIDChanged}
            value={authorizedID}
            disabled={authorizedDataConfirmed}
          ></Input>
        </Form.Field>

        {authorizedDataConfirmed ? (
          <SelectSPAndTimeSlot />
        ) : (
          <Form.Field>
            <Button
              positive
              visible={authorizedDataConfirmed}
              onClick={this.confirmAuthorizedInfo}
            >
              Confirm data
            </Button>
          </Form.Field>
        )}
      </Form>
    );
  }
}

function mapState(state) {
  const {
    authorizedDataConfirmed,
    authorizedName,
    authorizedID,
  } = state.servicePoint;
  return {
    authorizedDataConfirmed,
    authorizedName,
    authorizedID,
  };
}

const actionCreators = {
  authorizedPersonNameChanged: servicePointActions.authorizedPersonNameChanged,
  authorizedPersonIDChanged: servicePointActions.authorizedPersonIDChanged,
  confirmAuthorizedPersonInfo: servicePointActions.confirmAuthorizedPersonInfo,
};

const connectedAuthorizedPersonForm = connect(
  mapState,
  actionCreators
)(AuthorizedPersonForm);

export { connectedAuthorizedPersonForm as AuthorizedPersonForm };
