import React, { Component } from "react";
import { Card } from "semantic-ui-react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { withTranslation } from "react-i18next";
import { userActions } from "../../actions";
import "./ProfileCard.css";

class ProfileCard extends Component {
  componentDidMount() {
    this.props.getuserTransactionsData();
  }

  render() {
    const { t } = this.props;
    let balance = this.props.userTransactions.balance? Math.round(this.props.userTransactions.balance):"0"
    let credit = this.props.userTransactions.credit? Math.round(this.props.userTransactions.credit):"0"
    let debit = this.props.userTransactions.debit? Math.round(this.props.userTransactions.debit):"0"
    return (
      <div className="card" >
        <Link to="/transaction">
          <div className="cardsStyle">
            <Card.Content>
              <Card.Header className="cardHeader">
                <div className="userBalance">
                  <span for="currency-field">{t("Your Balance")}</span>
                  <div className="userBalanceAmount">
                    <h3>{balance}</h3>
                  </div>
                </div>
                <div className="userRevenue">
                  <span>{t("Revenue")}</span>
                  <h3>{credit}</h3>(
                  {this.props.userTransactions.onHold} {t("on hold")})
                </div>
                <div className="userRevenue">
                  <span>{t("Withdrawals")}</span>
                  <h3>{debit}</h3>(
                  {this.props.userTransactions.pending} {t("pending")})
                </div>
              </Card.Header>
            </Card.Content>
          </div>
          <div className="cardExtraStyle">
            <div className="userProfileData">
              <h3>{t("Registeration credit")}</h3>
              <div className="userPosts">
                <h3>{this.props.userTransactions.goldCoins}</h3>
                <div>{t("posts")}</div>
              </div>
            </div>
            <h1 className="ProfileCardLine"></h1>
            <div className="userProfileData">
              <h3>{t("Sold item credit")}</h3>
              <div className="userInvities">
                <h3>{this.props.userTransactions.silverCoins}</h3>
                <div>{t("invites")}</div>
              </div>
            </div>
          </div>
        </Link>
      </div>
    );
  }
}
function mapState(state) {
  const { userTransactions } = state.users;
  return { userTransactions };
}

const actionCreators = {
  getuserTransactionsData: userActions.userTransactions,
  changeLanguage: userActions.setLanguage,
};

const connectedProfileCardData = connect(mapState, actionCreators)(withTranslation()(ProfileCard));
export { connectedProfileCardData as ProfileCard };
