import { faqConstants } from "../constants";

const initialState = {
  faqs: [],
  loaded: false,
};

export function faqs(state = initialState, action) {
  switch (action.type) {
    case faqConstants.VIEW_ALL_FAQS_REQUEST:
      return {
        ...state,
        loaded: false,
      };
    case faqConstants.VIEW_ALL_FAQS_SUCCESS:
      return {
        ...state,
        faqs: action.faqs,
        loaded: true,
      };      
    case faqConstants.VIEW_ALL_FAQS_FAILURE:
      return {
        ...state,
        loaded: true,
      };
    default:
      return { ...state };
  }
}
