import { returnRequestsConstants } from "../constants";

const initialState = {
  sellerReturnRequests: [],
  buyerReturnRequests: [],
  loaded: false,
  currentReturnRequest:[],
};

export function returnRequests(state = initialState, action) {
  switch (action.type) {
    case returnRequestsConstants.LOAD_ALL_RETURN_REQS_REQUEST:
      return {
        ...state,
        loaded: false,
      };         
    case returnRequestsConstants.LOAD_ALL_RETURN_REQS_SUCCESS:
      return {
        ...state,
        sellerReturnRequests: action.sellerReturnRequests,
        buyerReturnRequests: action.buyerReturnRequests,
        loaded: true,
      };
    case returnRequestsConstants.LOAD_ALL_RETURN_REQS_FAILURE:
      return {
        ...state,
        loaded: true,
      };      

    case returnRequestsConstants.SELLER_DECISION_ACCEPT:
      return {
        ...state,
      };

    case returnRequestsConstants.SELLER_DECISION_REJECT:
      return {
        ...state,
      };

    case returnRequestsConstants.OPEN_INVESTIGATION_REQUEST:
      return {
        ...state,
      };
    case returnRequestsConstants.DO_NOT_OPEN_INVESTIGATION_REQUEST:
      return { ...state };

      case returnRequestsConstants.CANCEL_RETURN_REQUEST:
        return {...state}


        case returnRequestsConstants.EDIT_RETURN_REQUEST:
          return{...state}

    // View Current Return Request //
    case returnRequestsConstants.VIEW_RETURN_REQUEST_REQUEST:
          return{...state,loading:true}

    case returnRequestsConstants.VIEW_RETURN_REQUEST_SUCCESS:
          return{...state, currentReturnRequest:action.returnRequest, loading:false}

    case returnRequestsConstants.VIEW_RETURN_REQUEST_FAILURE:
          return{...state,loading:false}

     // Buyer Chooses Drop Off Info Return Request //
      case returnRequestsConstants.CONFIRM_DROP_OFF_RETURN_REQUEST:
      return{...state,loading:true}

      case returnRequestsConstants.CONFIRM_DROP_OFF_RETURN_SUCCESS:
            return{...state, currentReturnRequest:action.returnRequest, loading:false}

      case returnRequestsConstants.CONFIRM_DROP_OFF_RETURN_FAILURE:
            return{...state,loading:false}

      case returnRequestsConstants.CONFIRM_PICK_UP_RETURN_REQUEST:
      return{...state,loading:true}

      case returnRequestsConstants.CONFIRM_PICK_UP_RETURN_SUCCESS:
            return{...state, currentReturnRequest:action.returnRequest, loading:false}

      case returnRequestsConstants.CONFIRM_PICK_UP_RETURN_FAILURE:
            return{...state,loading:false}
      default:
      return {
        ...state,
      };
  }
}
