import React, { Component } from "react";
import { connect } from "react-redux";
import { postActions, validatorActions } from "../../actions";
import { Form, Grid, Message, Input, Select } from "semantic-ui-react";

const sellingOptions = [
  {
    key: 1,
    value: "auctionWithoutMinPrice",
    text: "Auction Without Minimum Price",
  },
  { key: 2, value: "auctionWithMinPrice", text: "Auction With Minimum Price" },
  { key: 3, value: "buyNow", text: "Buy Now" },
  {
    key: 4,
    value: "auctionWithMinPriceAndBuyNow",
    text: "Auction With Minimum Price And Buy Now",
  },
  {
    key: 5,
    value: "auctionWithoutMinPriceAndBuyNow",
    text: "Auction Without Minimum Price And Buy Now",
  },
];

const durationOptions = [
  { key: 1, value: 1, text: "1 Day" },
  { key: 2, value: 3, text: "3 Days" },
  { key: 3, value: 5, text: "5 Days" },
  { key: 4, value: 7, text: "7 Days" },
];

const planOptions = [
  { key: 1, value: "NoPlan", text: "No Plan" },
  { key: 2, value: "Gold", text: "Gold Boost" },
  { key: 3, value: "Silver", text: "Silver Boost" },
];
class Step5 extends Component {
  schema = {
    required: ["minPrice", "price", "bidIncrement"],
    properties: {
      minPrice: {
        title: "minPrice",
        type: "number",
        minimum: 1,
      },
      price: {
        title: "price",
        type: "number",
        minimum: 1,
      },
      bidIncrement: {
        title: "bidIncrement",
        type: "number",
        minimum: 1,
      },
    },
  };
  handleChange = (e, { name, value }) => {
    this.props.sellingDataChanged(name, value);
    if (name === "price" || name === "minPrice" || name === "bidIncrement")
      this.props.validateInput(this.schema, name, parseInt(value));
  };

  price = (name) => {
    const { sellingMethod } = this.props;
    switch (name) {
      case "price":
        return (
          sellingMethod === "" ||
          sellingMethod === "auctionWithoutMinPrice" ||
          sellingMethod === "auctionWithMinPrice"
        );
      case "minPrice":
        return (
          sellingMethod === "" ||
          sellingMethod === "auctionWithoutMinPrice" ||
          sellingMethod === "buyNow" ||
          sellingMethod === "auctionWithoutMinPriceAndBuyNow"
        );
      case "duration":
        return sellingMethod === "" || sellingMethod === "buyNow";
      case "bidIncrement":
        return sellingMethod === "" || sellingMethod === "buyNow";
      default:
        return true;
    }
  };

  render() {
    const {
      currentStep,
      sellingMethod,
      price,
      minPrice,
      duration,
      bidIncrement,
      errors,
      promotionPlan,
    } = this.props;

    return currentStep !== 5 ? null : (
      <Grid
        centered
        columns={2}
        style={{ padding: "2em", minHeight: 500 }}
        textAlign="center"
        verticalAlign="middle"
      >
        <Grid.Column>
          <Form.Field
            control={Select}
            name="sellingMethod"
            label="Selling Format"
            placeholder="Choose Selling Format..."
            value={sellingMethod}
            options={sellingOptions}
            onChange={this.handleChange}
          />

          <Form.Field>
            <label>Price</label>
            <Input
              name="price"
              min={1}
              type="number"
              size="large"
              placeholder="1000 EGP"
              value={price}
              onChange={this.handleChange}
              disabled={this.price("price")}
            />
          </Form.Field>
          {errors && errors.price && (
            <Message negative>
              <Message.Header>{errors.price}</Message.Header>
            </Message>
          )}
          <Form.Field>
            <label>Minimum Price</label>
            <Input
              type="number"
              size="large"
              placeholder="1000 EGP"
              value={minPrice}
              onChange={this.handleChange}
              disabled={this.price("minPrice")}
              name="minPrice"
              min={1}
            />
          </Form.Field>
          {errors && errors.minPrice && (
            <Message negative>
              <Message.Header>{errors.minPrice}</Message.Header>
            </Message>
          )}
          <Form.Field>
            <label>Bid Increment</label>
            <Input
              type="number"
              size="large"
              placeholder="1000 EGP"
              value={bidIncrement}
              onChange={this.handleChange}
              disabled={this.price("bidIncrement")}
              name="bidIncrement"
              min={1}
            />
          </Form.Field>
          {errors && errors.bidIncrement && (
            <Message negative>
              <Message.Header>{errors.bidIncrement}</Message.Header>
            </Message>
          )}

          <Form.Field
            control={Select}
            name="duration"
            label="Duration"
            placeholder="Choose Auction Duration..."
            value={duration}
            options={durationOptions}
            onChange={this.handleChange}
            disabled={this.price("duration")}
          />
          <Form.Field
            control={Select}
            name='promotionPlan'
            label='Promotion Plan'
            placeholder='Choose Promotion Plan...'
            value={promotionPlan}
            options={planOptions}
            onChange={this.handleChange}
          />
        </Grid.Column>
      </Grid>
    );
  }
}

function mapState(state) {
  const { currentStep } = state.post;
  const { valid, errors } = state.validation;

  const {
    sellingMethod,
    price,
    minPrice,
    duration,
    bidIncrement,
    promotionPlan,
  } = state.post.sellingData;
  return {
    valid,
    errors,
    currentStep,
    sellingMethod,
    price,
    minPrice,
    bidIncrement,
    duration,
    promotionPlan,
  };
}

const actionCreators = {
  sellingDataChanged: postActions.sellingDataChanged,
  validateInput: validatorActions.validateInput,
};

const connectedStep5 = connect(mapState, actionCreators)(Step5);
export { connectedStep5 as Step5 };
