import React from "react";
import { Modal, Button } from "semantic-ui-react";

const EditPostModal = ({
    editWholePostSuccess,
    handleCloseModal,
}) => {
    return (
        <Modal
          onClose={handleCloseModal}
          open={editWholePostSuccess}
        >
          <Modal.Header>Post Edit Successful</Modal.Header>
          <Modal.Content image>
            <Modal.Description>
              <p>
                Post has been modified Successfully!
              </p>
              <p>Would you like to post it live ? Or stay offline ?</p>
            </Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button color='black' onClick={() => handleCloseModal(false)}>
              Stay Offline
            </Button>
            <Button
              content="Go Online"
              labelPosition='right'
              icon='checkmark'
              onClick={() => handleCloseModal(true)}
              positive
            />
          </Modal.Actions> 
        </Modal>
      )
}

export default EditPostModal;
