import React, { Component } from "react";
import { Segment, Container, Form, Message } from "semantic-ui-react";
import { connect } from "react-redux";
import { validatorActions, contactUsActions } from "../../actions";
import DesktopBreakpoint from "../../components/Responsive_utillities/desktop_breakPoint";
import PhoneBreakpoint from "../../components/Responsive_utillities/phone_breakPoint";
import "./contactForm.css";
import { withTranslation } from "react-i18next";
class ContactForm extends Component {
  schema = {
    required: ["name", "email", "subject"],
    properties: {
      name: { title: "Name", type: "string", minLength: 5, maxLength: 50 },
      email: {
        title: "Email",
        type: "string",
        format: "email",
        minLength: 5,
        maxLength: 255,
      },
      subject: {
        title: "Subject",
        type: "string",
        minLength: 5,
        maxLength: 50,
      },
      message: {
        title: "Message",
        type: "string",
        minLength: 0,
        maxLength: 500,
      },
      phone: {
        title: "Phone",
        type: "string",
        pattern: "^01[0-2|5]{1}[0-9]{8}$",
      },
    },
  };
  state = {
    contactus: {
      name: "",
      email: "",
      country: "",
      subject: "",
      message: "",
      phone: "",
    },
    submittedValid: false,
    noSubmission: true,
  };

  handleChange = (e, { name, value }) => {
    this.props.validateInput(this.schema, name, value);
    const contactus = { ...this.state.contactus };
    contactus[name] = value;
    this.setState({ contactus });
  };
  errorValidation = (name) => {
    return this.props.errors && this.props.errors[name];
  };

  handleSubmit = async (e) => {
    this.setState({ submittedValid: false });

    e.preventDefault();
    const { name, email, subject, message, phone } = this.state.contactus;
    await this.props.validateForm(this.schema, {
      name,
      email,
      subject,
      message,
      phone,
    });

    if (!this.props.valid) return;

    this.setState({ noSubmission: false, submittedValid: true });
    await this.props.contactUsForm(this.state.contactus);
  };

  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    const { errors } = this.props;
    return (
      <Container>
        <DesktopBreakpoint name="desktop">
          <Container>
            <Segment className={`ContactForm ${language}`}>
              <div className={`largScreenFormStyle ${language}`}>
                <h3>{t("landingPage:contactUs")}</h3>
                <span>{t("landingPage:requiredFields")}</span>
                <Form>
                  <Form.Field>
                    <div className="FormStyle">
                      <div className="formInputTitle">
                        <label>{t("landingPage:name")}</label>
                        {this.errorValidation("name") && (
                          <span>{t("landingPage:senderNameIsRequired")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      required
                      disabled={!this.state.noSubmission}
                      name="name"
                      value={this.state.contactus.name}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:formInputName")}
                    ></Form.Input>
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div className="formInputTitle">
                        <label>{t("landingPage:formSubject")}</label>
                        {this.errorValidation("subject") && (
                          <span>{t("landingPage:subjectIsRequired")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      disabled={!this.state.noSubmission}
                      name="subject"
                      value={this.state.contactus.subject}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:messageSubject")}
                      required
                    ></Form.Input>
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div>
                        <label>{t("landingPage:message")}</label>
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.TextArea
                      placeholder={t("landingPage:messagePlaceHolder")}
                      value={this.state.contactus.message}
                      name="message"
                      onChange={this.handleChange}
                      error={errors && errors.message}
                    />
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div>
                        <label>{t("landingPage:formEmail")}</label>
                        {this.errorValidation("email") && (
                          <span>{t("landingPage:wrongEmail")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      required
                      disabled={!this.state.noSubmission}
                      name="email"
                      value={this.state.contactus.email}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:emailPlaceHolder")}
                    ></Form.Input>
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div>
                        <label>{t("landingPage:mobilenumberInput")}</label>
                        {this.errorValidation("phone") && (
                          <span>{t("landingPage:wrongMobile")}</span>
                        )}
                      </div>
                      <div className="optional">
                        {t("landingPage:optionalField")}
                      </div>
                    </div>
                    <Form.Input
                      required
                      disabled={!this.state.noSubmission}
                      name="phone"
                      value={this.state.contactus.phone}
                      placeholder={t("landingPage:mobilenumberInput")}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                  {Object.keys(errors).length > 0 && (
                    <p>{t("landingPage:errorMessage")}</p>
                  )}
                  <Form.Button
                    disabled={Object.keys(errors).length > 0}
                    onClick={this.handleSubmit}
                  >
                    <span>{t("landingPage:submit")}</span>
                  </Form.Button>
                </Form>
                {this.state.submittedValid && (
                  <Message style={{ textAlign: "center" }} success>
                    {t("landingPage:submittedValid")}
                  </Message>
                )}
              </div>
            </Segment>
          </Container>
        </DesktopBreakpoint>

        <PhoneBreakpoint name="phone">
          <Container>
            <div className="startingLine"></div>
            <Segment className={`ContactForm ${language}`}>
              <h3>{t("landingPage:contactUs")}</h3>
              <span>{t("landingPage:requiredFields")}</span>
              <Form>
                <Form.Group widths="equal">
                  <Form.Field>
                    <div className="FormStyle">
                      <div className="formInputTitle">
                        <label>{t("landingPage:name")}</label>
                        {this.errorValidation("name") && (
                          <span>{t("landingPage:senderNameIsRequired")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      required
                      disabled={!this.state.noSubmission}
                      name="name"
                      value={this.state.contactus.name}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:formInputName")}
                    ></Form.Input>
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div className="formInputTitle">
                        <label>{t("landingPage:formSubject")}</label>
                        {this.errorValidation("subject") && (
                          <span>{t("landingPage:subjectIsRequired")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      disabled={!this.state.noSubmission}
                      name="subject"
                      value={this.state.contactus.subject}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:messageSubject")}
                      required
                    ></Form.Input>
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div>
                        <label>{t("landingPage:message")}</label>
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.TextArea
                      // label={t("landingPage:message")}
                      placeholder={t("landingPage:messagePlaceHolder")}
                      value={this.state.contactus.message}
                      name="message"
                      onChange={this.handleChange}
                      error={errors && errors.message}
                    />
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div>
                        <label>{t("landingPage:formEmail")}</label>
                        {this.errorValidation("email") && (
                          <span>{t("landingPage:wrongEmail")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      required
                      disabled={!this.state.noSubmission}
                      name="email"
                      value={this.state.contactus.email}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:emailPlaceHolder")}
                    ></Form.Input>
                  </Form.Field>
                  <Form.Field>
                    <div className="FormStyle">
                      <div>
                        <label>{t("landingPage:mobilenumberInput")}</label>
                        {this.errorValidation("phone") && (
                          <span>{t("landingPage:wrongMobile")}</span>
                        )}
                      </div>
                      <div className="optional">
                        {t("landingPage:optionalField")}
                      </div>
                    </div>
                    <Form.Input
                      required
                      disabled={!this.state.noSubmission}
                      name="phone"
                      value={this.state.contactus.phone}
                      placeholder={t("landingPage:mobilenumberInput")}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                </Form.Group>

                {Object.keys(errors).length > 0 && (
                  <p>{t("landingPage:errorMessage")}</p>
                )}
                <Form.Button
                  disabled={Object.keys(errors).length > 0}
                  onClick={this.handleSubmit}
                >
                  <span>{t("landingPage:submit")}</span>
                </Form.Button>
              </Form>
              {this.state.submittedValid && (
                <Message style={{ textAlign: "center" }} success>
                  {" "}
                  {t("landingPage:submittedValid")}
                </Message>
              )}
            </Segment>
          </Container>
        </PhoneBreakpoint>
      </Container>
    );
  }
}
function mapState(state) {
  const { errors, valid } = state.validation;
  const { alert } = state;
  return { errors, valid, alert };
}

const actionCreators = {
  validateInput: validatorActions.validateInput,
  validateForm: validatorActions.validateForm,
  contactUsForm: contactUsActions.contactUsForm,
};

const connectedContactUs = connect(
  mapState,
  actionCreators
)(withTranslation("landingPage")(ContactForm));

export { connectedContactUs as ContactForm };
