import { badgesConstants } from '../constants';

const initialState = {
    optedBadges:[],
    badgesToOpt:[]
};

export function badges(state = initialState, action) {
    switch (action.type) {
        ///////////////////////OPTED BADGES/////////////////////
        case badgesConstants.VIEW_OPTED_BADGES_REQUEST:
            return {
                ...state,
            };
        case badgesConstants.VIEW_OPTED_BADGES_SUCCESS:
            return{
                ...state,
                optedBadges:action.badgeData
            }
        case badgesConstants.VIEW_OPTED_BADGES_FAILURE:
            return {
                ...state,
            };
        /////////Badges not yet opted by the user///////
        case badgesConstants.VIEW_BADGES_TO_OPT_REQUEST:
            return {
                ...state,
            };  
        case badgesConstants.VIEW_BADGES_TO_OPT_SUCCESS:
            return{
                ...state,
                badgesToOpt:action.badgeData
            }
        case badgesConstants.VIEW_BADGES_TO_OPT_FAILURE:
            return {
                ...state,
            };
        ////////////     DEFAULT      /////////////////
        default:
            return {
                ...state,
            };
    }
}
