import { httpService } from './';
import { apiUrl } from '../config';

export const shoppingCartService = {
    viewShoppingCart,
    updateQuantity,
    removeItem,
    clearCart,
    resetItem,
};

const apiEndpoint = apiUrl + '/shoppingCart';

function viewShoppingCart() {
    return httpService.get(apiEndpoint);
}

function updateQuantity(postId, newQuantity, buyingMethod) {
    return httpService.put(apiEndpoint + `/update/${postId}/${newQuantity}/${buyingMethod}`);
}

function removeItem(postId) {
    return httpService.put(apiEndpoint + `/remove/${postId}`);
}

function clearCart() {
    return httpService.put(apiEndpoint + '/clear');
}

function resetItem(postId){
    return httpService.put(apiEndpoint+`/resetItem/${postId}`)
}
