import React, { Component } from "react";
import { Container, Image, Segment } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import { imageUrl } from "./config";

 class Safety extends Component {
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container>
        <Segment className={`generalSection ${language}`}>
          <Image
            src={`${imageUrl}/landing-page/background@3x.png`}
            className="backgroundImg"
            loading="lazy"
          />
          <Image
            src={`${imageUrl}/landing-page/Safety@3x-min.png`}
            className="ImgStyle"
            loading="lazy"
          />
        </Segment>
        <Segment className={`seftySection ${language}`}>
          <h4> {t('landingPage:safetyMainTitle')} </h4>
          <span> {t('landingPage:safetySubTitle')} </span>
          <p>
            {t('landingPage:safetyPragraph')}
          </p>
        </Segment>
      </Container>
    );
  }
}
export default withTranslation("landingPage")(Safety);
