import { faqService } from "../services";
import { alertActions } from "./";
import { faqConstants } from "../constants";

export const faqActions = {
  loadAllFAQ,
};

function loadAllFAQ() {
  return async dispatch => {
    await dispatch(request());
    try {
      const {data} = await faqService.viewFAQ();
      dispatch(success(data));
    } catch (ex) {
      dispatch(failure(ex));
      dispatch(
        alertActions.error({
          header: "Couldn't load FAQs",
          content: ex.message
        })
      );
    }
  };

  function request() {
    return {
      type: faqConstants.VIEW_ALL_FAQS_REQUEST,
    };
  }

  function success(data) {
    return {
      type: faqConstants.VIEW_ALL_FAQS_SUCCESS,
      faqs: data,
    };
  }

  function failure(error) {
    return {
      type: faqConstants.VIEW_ALL_FAQS_FAILURE,
      error
    };
  }
}
