import { keepInTouchConstant } from "../constants";
import {  invitationService } from "../services";

export const keepInTouchAction = {
    keepInTouch
};

function keepInTouch(formObj) {
    return async (dispatch) => {
      await dispatch(request());
      try {
        const { data } = await invitationService.keepInTouchRequest(formObj);
        dispatch(success(data));
      } catch (ex) {
        dispatch(failure(ex));  
      }
    };
    function request() {
      return { type: keepInTouchConstant.KEEP_IN_TOUCH_REQUEST };
    }
  
    function success(data) {
      return {
        type: keepInTouchConstant.KEEP_IN_TOUCH_SUCCESS,
        data
      };
    }
    function failure(error) {
      return { type: keepInTouchConstant.KEEP_IN_TOUCH_FAILURE, error };
    }
  }
