import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Button, Modal, Input, Grid, Icon } from 'semantic-ui-react';
import { userActions } from '../../actions/user.actions';

class UpdateEmailModal extends Component {
    state = {
        emailAddress: '',
        enableSubmit: false,
    };
    show = () => () => this.props.showUpdateEmailModal(true);
    close = () => this.props.showUpdateEmailModal(false);
    emailAddressChanged = (e, data) => {
        const enableSubmit = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(
            data.value,
        );
        this.setState({
            emailAddress: data.value,
            enableSubmit: enableSubmit,
        });
    };
    render() {
        const { openUpdateEmail, userEmail } = this.props;
        return (
            <div>
                <Button primary onClick={this.show()}>{userEmail.address ? 'Edit' : 'Add'}</Button>
                <Modal open={openUpdateEmail} onClose={this.close} closeIcon>
                    <Modal.Header>Update email addresss</Modal.Header>
                    <Modal.Content>
                        <Grid>
                            <Grid.Row>
                                <Grid.Column width={3}>
                                    Email address :
                                </Grid.Column>
                                <Grid.Column width={5}>
                                    <Input
                                        placeholder='new email address'
                                        onChange={this.emailAddressChanged}
                                    />
                                    {this.state.enableSubmit ? (
                                        <Icon
                                            color='green'
                                            name='checkmark'
                                            size='small'
                                        />
                                    ) : (
                                        <Icon
                                            color='yellow'
                                            name='warning'
                                            size='small'
                                        />
                                    )}
                                </Grid.Column>
                            </Grid.Row>
                        </Grid>
                    </Modal.Content>
                    <Modal.Actions>
                        <Button.Group>
                            <Button onClick={this.close}>Cancel</Button>
                            <Button.Or />
                            {this.state.enableSubmit ? (
                                <Button
                                    positive
                                    onClick={() => {
                                        this.props.handleUpdateEmailButtonClicked(
                                            this.state.emailAddress,
                                        );
                                    }}
                                >
                                    Save
                                </Button>
                            ) : (
                                <Button positive disabled>
                                    Save
                                </Button>
                            )}
                        </Button.Group>
                    </Modal.Actions>
                </Modal>
            </div>
        );
    }
}

function mapState(state) {
    const { openUpdateEmail } = state.users;
    return { openUpdateEmail };
}

const actionCreators = {
    handleUpdateEmailButtonClicked: userActions.updateEmail,
    showUpdateEmailModal: userActions.showUpdateEmailModal,
};

const connectedUpdateEmailModal = connect(
    mapState,
    actionCreators,
)(UpdateEmailModal);

export { connectedUpdateEmailModal as UpdateEmailModal };
