import React from "react";
import { Button, Modal, Segment, Image } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import { imageUrl } from "../LandingPage/config";

function exampleReducer(state, action) {
  switch (action.type) {
    case "OPEN_MODAL":
      return { open: true, dimmer: action.dimmer };
    case "CLOSE_MODAL":
      return { open: false };
    default:
      throw new Error();
  }
}

function ModalExampleDimmer(props) {
  const [state, dispatch] = React.useReducer(exampleReducer, {
    open: false,
    dimmer: undefined,
  });
  const { open, dimmer } = state;
  const language = localStorage.getItem("i18nextLng");
  const { t, submit, phone, keepInTouchState } = props;

  return (
    <div>
      <Button
        onClick={() => (
          dispatch({ type: "OPEN_MODAL", dimmer: "blurring" }), submit()
        )}
      >
        <p className="FormButtonSpan">{t("notInvited:notInvitedFormButton")}</p>
      </Button>

      {keepInTouchState?.success !== undefined ? (
        <Modal
          dimmer={dimmer}
          open={open}
          onClose={() => dispatch({ type: "CLOSE_MODAL" })}
        >
          <Segment className={`notInvitedPopup ${language}`}>
            {keepInTouchState.success ? (
              <Segment className={`notInvitedPopupHeader ${language}`}>
                <h4> {t("notInvited:popUpHeader")} </h4>
                <div className="IconCircle">
                  <Image src={`${imageUrl}/landing-page/check-circle-outline-round@3x-min.png`} />
                  <span>{t("notInvited:popUpHeaderMoreDetalils")}</span>
                </div>
              </Segment>
            ) : (
              <Segment className={`notInvitedPopupHeaderError ${language}`}>
                <h4> {t("notInvited:popUpHeaderError")} </h4>
                <div className="IconCircle">
                  <Image src={`${imageUrl}/landing-page/Cross%20Circle@3x.png`} />
                  <span>{t("notInvited:popUpHeaderMoreDetalilsError")}</span>
                </div>
              </Segment>
            )}

            {!keepInTouchState.success ? (
              phone ? (
                <p>
                  {t("popUpErrorMessagePhoneNum", {
                    phone: `${props.phone}`,
                  })}
                </p>
              ) : (
                <p>
                  {t("popUpErrorMessageEmail", {
                    email: `${props.email}`,
                  })}
                </p>
              )
            ) : phone ? (
              <p>
                {t("submissionMessageByPhone", {
                  name: `${props.name}`,
                  phone: `${props.phone}`,
                })}
              </p>
            ) : (
              <p>
                {t("submissionMessageByEmail", {
                  name: `${props.name}`,
                  email: `${props.email}`,
                })}
              </p>
            )}

            <Modal.Actions>
              <Button
                onClick={() => dispatch({ type: "CLOSE_MODAL" })}
                className="popupBtn"
              >
                {t("notInvited:popUpButton")}
              </Button>
            </Modal.Actions>
          </Segment>
        </Modal>
      ) : (
        <div />
      )}
    </div>
  );
}

export default withTranslation("notInvited")(ModalExampleDimmer);
