import React, { Component } from "react";
import { Segment, Image, Container, Button } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import CheckCircle from "../../../images/check-circle-outline-round-24px@3x-min.png";
class registrationConfirmation extends Component {
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container>
        <Segment className={`registrationConfirmation ${language}`}>
          <h4>{t("registrationConfirmationTitle")}</h4>
          <div className="registrationCheck">
            <Image src={CheckCircle} />
            <p className="ConfirmationSubtitle">{t("registrationConfirmationSubtitle")}</p>
          </div>
          
          <div className={`AppSignIn ${language}`}>
          <p className="AppSignIn">{t("appSignIn")}</p>

            <a
                href="https://mazadat.onelink.me/zXMK/useapp"
                target="_blank"
                rel="noopener noreferrer"
              >
            <Button>{t("appSignInButton")}</Button>
            </a>
        </div>
        </Segment>
        <div className="borderBottom"></div>
        
      </Container>
    );
  }
}
export default withTranslation("registerPage")(registrationConfirmation);
