import { postConstants } from "../constants";

const getIntialState = () => {
  return {
    posts: [],
    upToPosts: [],
    discountedPosts: [],
    mostViewedPosts: [],
    postsWithMinQuantities: [],
    EndingSoonPosts: [],
    HotAuctionsPosts: [],
    loading: false,
    error: {},
  };
};

export function home(state = getIntialState(), action) {
  switch (action.type) {
    case postConstants.POST_GETALL_REQUEST:
      return { ...state, loading: true };
    case postConstants.POST_GETALL_SUCCESS:
      return { ...state, posts: action.posts, loading: false };
    case postConstants.POST_GETALL_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.GET_UPTO_REQUEST:
      return { ...state, loading: true };
    case postConstants.GET_UPTO_SUCCESS:
      return { ...state, upToPosts: action.posts, loading: false };
    case postConstants.GET_UPTO_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.GET_ACTIVITIES_REQUEST:
      return { ...state, loading: true };
    case postConstants.GET_ACTIVITIES_SUCCESS:
      return {
        ...state,
        discountedPosts: action.posts,
        loading: false,
      };
    case postConstants.GET_ACTIVITIES_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.GET_MOSTVIEWEDPOSTS_REQUEST:
      return { ...state, loading: true };
    case postConstants.GET_MOSTVIEWEDPOSTS_SUCCESS:
      return {
        ...state,
        mostViewedPosts: action.posts,
        loading: false,
      };
    case postConstants.GET_MOSTVIEWEDPOSTS_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.GET_POSTSWITHMINQUANTITIES_REQUEST:
      return { ...state, loading: true };
    case postConstants.GET_POSTSWITHMINQUANTITIES_SUCCESS:
      return {
        ...state,
        postsWithMinQuantities: action.posts,
        loading: false,
      };
    case postConstants.GET_POSTSWITHMINQUANTITIES_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.GET_ENDINGSOONPOSTS_REQUEST:
      return { ...state, loading: true };
    case postConstants.GET_ENDINGSOONPOSTS_SUCCESS:
      return {
        ...state,
        EndingSoonPosts: action.posts,
        loading: false,
      };
    case postConstants.GET_ENDINGSOONPOSTS_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.GET_HotAuctionsPosts_REQUEST:
      return { ...state, loading: true };
    case postConstants.GET_HotAuctionsPosts_SUCCESS:
      return { ...state, HotAuctionsPosts: action.posts, loading: false };
    case postConstants.GET_HotAuctionsPosts_FAILURE:
      return { ...state, error: action.error, loading: false };

    case postConstants.CLEAR:
      return getIntialState();
    default:
      return state;
  }
}
