import {
  Button,
  Form,
  Grid,
  Header,
  Message,
  Segment,
} from "semantic-ui-react";
import React, { Component } from "react";
import { connect } from "react-redux";
import { passwordActions, validatorActions } from "../../actions";

class ResetPassword extends Component {
  state = { password: "", message: "" };
  schema = {
    required: ["password"],
    properties: {
      password: {
        title: "Password",
        type: "string",
        minLength: 8,
        maxLength: 1024,
      },
    },
  };
  handleChange = (e, { name, value }) => {
    this.props.validateInput(this.schema, name, value);
    this.setState({ [name]: value });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { password } = this.state;

    await this.props.resetPassword({
      token: this.props.match.params.token,
      password: password,
    });
  };
  render() {
    const { alert, errors } = this.props;
    const { password } = this.state;

    return (
      <Grid
        textAlign="center"
        style={{ height: "100vh" }}
        verticalAlign="middle"
      >
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as="h2" color="teal" textAlign="center">
            New Password
          </Header>
          <Form size="large" onSubmit={this.handleSubmit}>
            <Segment>
              <Form.Input
                name="password"
                size="large"
                icon="lock"
                iconPosition="left"
                placeholder="Password"
                value={password}
                type="password"
                onChange={this.handleChange}
                error={errors && errors.password}
              />

              <Button color="teal" fluid>
                Reset Password
              </Button>
            </Segment>
          </Form>
          <Message
            error={alert.type === "error"}
            success={alert.type === "success"}
          >
            {alert.type==="success" ? alert.content.message: alert.content }
          </Message>
        </Grid.Column>
      </Grid>
    );
  }
}

function mapState(state) {
  const { alert } = state;
  const { valid, errors } = state.validation;

  return { alert, valid, errors };
}

const actionCreators = {
  resetPassword: passwordActions.resetPassword,
  validateInput: validatorActions.validateInput,
  validateForm: validatorActions.validateForm,
};

const connectedResetPassword = connect(mapState, actionCreators)(ResetPassword);
export { connectedResetPassword as ResetPasswordPage };
