import { shoppingCartConstants } from '../constants';

const initialState = {
    shoppingCartData: { posts: [] },
};

export function shoppingCart(state = initialState, action) {
    switch (action.type) {
        case shoppingCartConstants.VIEW_CART_REQUEST:
            return {
                ...state,
            };
        case shoppingCartConstants.VIEW_CART_SUCCESS:
            return {
                ...state,
                shoppingCartData: action.shoppingCartData,
            };
        case shoppingCartConstants.VIEW_CART_FAILURE:
            return {
                ...state,
            };

        case shoppingCartConstants.ADD_TO_CART_REQUEST:
            return {
                ...state,
            };
        case shoppingCartConstants.ADD_TO_CART_SUCCESS:
            return {
                ...state,
                shoppingCartData: action.shoppingCartData,
            };
        case shoppingCartConstants.ADD_TO_CART_FAILURE:
            return {
                ...state,
            };

        case shoppingCartConstants.UPDATE_QUANTITY_CART_REQUEST:
            return {
                ...state,
            };
        case shoppingCartConstants.UPDATE_QUANTITY_CART_SUCCESS:
            let newState = {
                ...state,
                shoppingCartData: action.shoppingCartData,
            };
            return newState;
        case shoppingCartConstants.UPDATE_QUANTITY_CART_FAILURE:
            return {
                ...state,
            };

        case shoppingCartConstants.REMOVE_ITEM_CART_REQUEST:
            return {
                ...state,
            };
        case shoppingCartConstants.REMOVE_ITEM_CART_SUCCESS:
            let updatedState = { ...state };
            const filteredPosts = updatedState.shoppingCartData.posts.filter(
                function(item, index, arr) {
                    return item.post._id !== action.postId;
                },
            );
            updatedState.shoppingCartData.posts = filteredPosts;
            return updatedState;
        case shoppingCartConstants.REMOVE_ITEM_CART_FAILURE:
            return {
                ...state,
            };

        case shoppingCartConstants.REMOVE_ALL_ITEMS_CART_REQUEST:
            return {
                ...state,
            };
        case shoppingCartConstants.REMOVE_ALL_ITEMS_CART_SUCCESS:
            return { ...state, shoppingCartData: { posts: [] } };
        case shoppingCartConstants.REMOVE_ALL_ITEMS_CART_FAILURE:
            return {
                ...state,
            };

        case shoppingCartConstants.PROCEED_TO_CHECKOUT_REQUEST:
            return {
                ...state,
            };

        case shoppingCartConstants.PROCEED_TO_CHECKOUT_SUCCESS:
            return {
                ...state,
            };

        case shoppingCartConstants.PROCEED_TO_CHECKOUT_FAILURE:
            return {
                ...state,
            };

        default:
            return {
                ...state,
            };
    }
}
