import { termsandConditionsConstants } from '../constants';

const initialState = {
    termsandconditions: {},
    error: '',
    loading: false,
}

export function termsandConditions(state = initialState, action) {
    switch (action.type) {
        case termsandConditionsConstants.VIEW_TERMS_AND_CONDITIONS_REQUEST:
            return { ...state, loading: true };
        case termsandConditionsConstants.VIEW_TERMS_AND_CONDITIONS_SUCCESS:
            return {...state,termsandConditions: action.termsandConditions, loading: false };
        case termsandConditionsConstants.VIEW_TERMS_AND_CONDITIONS_FAILURE:
            return { ...state,error: action.errors.message, loading: false };
    
            default:
            return { ...state };
    }
}
