import React, { Component } from 'react';
import { connect } from 'react-redux';
import { orderActions } from '../../actions';
import { Grid, Menu } from 'semantic-ui-react';
import { OrdersAsBuyer } from './OrdersAsBuyer';
import { OrdersAsSeller } from './OrdersAsSeller';
import { orderConstants } from '../../constants';


class OrdersPage extends Component {
    async componentDidMount() {
        //Get also if order requested for return
        await  this.props.viewAllOrders();
    }

    handleViewOrdersAsBuyerClicked = () => {
        this.props.handleViewOrdersAsBuyerClicked();
    }

    handleViewOrdersAsSellerClicked = () => {
        this.props.handleViewOrdersAsSellerClicked();
    }

    render() {
        const { ordersDisplayed,sellerReturnRequests,buyerReturnRequests } = this.props;
        
        return (<Grid style={{ padding: '1em 10em' }}>
            <Grid.Row>
                <Grid.Column width={2} style={{ padding: '6em 0em' }}>
                    <Menu fluid vertical>
                        <Menu.Item
                            active={
                                this.props.display === orderConstants.VIEW_ORDERS_AS_BUYER
                            }
                            onClick={this.handleViewOrdersAsBuyerClicked
                            }
                        >
                            Placed Orders
                                </Menu.Item>
                        <Menu.Item
                            active={this.props.display === orderConstants.VIEW_ORDERS_AS_SELLER}
                            onClick={
                                this.handleViewOrdersAsSellerClicked
                            }
                        >
                            Requested Orders
                                </Menu.Item>
                    </Menu>
                </Grid.Column>
                <Grid.Column width={9}>
                    {ordersDisplayed === orderConstants.VIEW_ORDERS_AS_BUYER  ?
                        <OrdersAsBuyer sellerReturnRequests={sellerReturnRequests} buyerReturnRequests={buyerReturnRequests}/> : <OrdersAsSeller />}
                </Grid.Column>
            </Grid.Row>
        </Grid>);
    }
}

function mapState(state) {
    const { orderHistory, ordersDisplayed } = state.order;
    const{sellerReturnRequests,buyerReturnRequests,loaded}=state.returnRequests;
    return {
        orderHistory,
        ordersDisplayed,
        sellerReturnRequests,
        buyerReturnRequests,
        loaded,
    };
}

const actionCreators = {
    viewAllOrders: orderActions.viewAllOrders,
    handleViewOrdersAsBuyerClicked: orderActions.handleViewOrdersAsBuyerClicked,
    handleViewOrdersAsSellerClicked: orderActions.handleViewOrdersAsSellerClicked,
};

const connectedOrdersPage = connect(mapState, actionCreators)(OrdersPage);

export { connectedOrdersPage as OrdersPage };
