import { questionConstants } from "../constants";

const initialState = {
  questions: [],
  postquestions:{}
};

export function questions(state = initialState, action) {
  switch (action.type) {
    case questionConstants.GETQUESTIONSTOUSERREQUEST_REQUEST:
      return { ...state };
    case questionConstants.GETQUESTIONSTOUSERREQUEST_SUCCESS:
      return {
        ...state,
        questions: action.data[0],
      };

    case questionConstants.GETQUESTIONSTOUSERREQUEST_FAILURE:
      return { ...state };

      case questionConstants.FETCH_POST_QUESTIONS_REQUEST:
        return { ...state };
      case questionConstants.FETCH_POST_QUESTIONS_SUCCESS:
        return {
          ...state,
          postquestions: action.postquestions,
        };
  
      case questionConstants.FETCH_POST_QUESTIONS_FAILURE:
        return { ...state };
    default:
      return state;
  }
}
