import React, { Component } from "react";
import { Container, Image, Segment } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import { imageUrl } from "./config";
class Reliability extends Component {
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container>
        <Segment className={`generalSection ${language}`}>
          <Image
            src={`${imageUrl}/landing-page/Reliabilitybackground@3x.png`}
            className="backgroundImg"
            loading="lazy"
          />
          <Image
            src={`${imageUrl}/landing-page/Reliability@3x-min.png`}
            className="ImgStyle"
            loading="lazy"
          />
        </Segment>
        <Segment className={`ReliabilitySection ${language}`}>
          <h4> {t("landingPage:reliabilityMainTitle")} </h4>
          <span> {t("landingPage:reliabilitySubTitle")} </span>
          <p>{t("landingPage:reliabilityPragraph")}</p>
        </Segment>
      </Container>
    );
  }
}
export default withTranslation("landingPage")(Reliability);
