exports.onSale = (discount) => {
    if (!discount || !discount.discount_value) return false;
    if (new Date() > new Date(discount.valid_until)) return false;

    return true;
}

exports.calculatePrice = (price, discount) => {
    const { discount_unit, discount_value } = discount;
    switch (discount_unit) {
    case 'percentage':
        return price - (price * (discount_value / 100));
    case 'amount': {
        const discountedPrice = price - discount_value;
        return discountedPrice > 0 ? discountedPrice : 0;
    }
    default:
        return price;
    }
};
