import { httpService } from "./";
import { apiUrl } from "../config";

export const badgesService = {
  viewOptedBadges,
  activateBadge,
  viewOptableBadges,
};

const apiEndpoint = apiUrl + "/badge";

function viewOptedBadges() {
  return httpService.get(`${apiEndpoint}/opted`);
}
function activateBadge(badgeID) {
    return httpService.put(`${apiEndpoint}/activate/${badgeID}`);
  }
function viewOptableBadges(){
  return httpService.get(`${apiEndpoint}/badgesToOpt`)
}
