import React, { Component } from "react";
import * as moment from "moment";
import {
  Label,
  Card,
  Segment,
  Divider,
  Button,
  Popup,
  Accordion,
  Icon,
  Form,
} from "semantic-ui-react";
import { Link } from "react-router-dom";
import { returnRequestsActions } from "../../actions";
import { validatorActions } from "../../actions";
import { connect } from "react-redux";
class ReturnRequestCard extends Component {
  state = {
    activeIndex: -1,
    reason: " ",
    submitted: false,
    rejectReason: "",
    buyerInvestigationNotes: "",
  };
  schema = {
    required: ["reason"],
    properties: {
      reason: { title: "reason", type: "string", minLength: 5, maxLength: 500 },
    },
  };

  schemaRejectReason = {
    required: ["rejectReason"],
    properties: {
      rejectReason: {
        title: "rejectReason",
        type: "string",
        minLength: 1,
        maxLength: 500,
      },
    },
  };

  handleChangeReason = (e, { name, value }) => {
    this.props.validateInput(this.schemaRejectReason, name, value);
    this.setState({ [name]: value });
  };

  handleRejectRequest = async () => {
    const { rejectReason } = this.state;
    await this.props.validateForm(this.schemaRejectReason, {
      rejectReason,
    });
    if (!this.props.valid) return;
    const body = { reason: rejectReason };
    await this.props.sellerDecision("reject", this.props.id, body);
    window.location.reload();
  };
  handleClick = (e) => {
    const { activeIndex } = this.state;
    const newIndex = activeIndex === 0 ? -1 : 0;

    this.setState({ activeIndex: newIndex });
  };

  handleChange = (e, { name, value }) => {
    this.props.validateInput(this.schema, name, value);

    this.setState({ reason: value });
  };

  handleSubmit = async (e) => {
    e.preventDefault();

    const { reason } = this.state;
    await this.props.validateForm(this.schema, reason);

    if (!this.props.valid) {
      return;
    }

    const body = { reason: reason };
    if (body.reason.length >= 5) {
      await this.props.editReturnRequest(this.props.id, body);

      this.setState({ activeIndex: -1 });
    }
  };

  handleChangeNotes = (e, { name, value }) => {
    this.props.validateInput(this.schemaBuyerInvestigationNotes, name, value);
    this.setState({ [name]: value });
  };

  handleBuyerInvestigationDecision = async (decision) => {
    const body = { buyerNotes: this.state.buyerInvestigationNotes };

    if (decision === "request" && this.props.id) {
      await this.props.investigationDecision("request", this.props.id, body);
    } else if (decision === "ignore" && this.props.id) {
      await this.props.investigationDecision("ignore", this.props.id, {
        buyerNotes: "",
      });
    }
  };

  schemaBuyerInvestigationNotes = {
    required: ["buyerInvestigationNotes"],
    properties: {
      buyerInvestigationNotes: {
        title: "buyerInvestigationNotes",
        type: "string",
        minLength: 1,
        maxLength: 500,
      },
    },
  };
  formatDate(date) {
    return moment(date).format("YYYY-MM-DD HH:mm:ss");
  }
  render() {
    const fullDate = this.props.date + "";
    const dividedDate = fullDate.split("T");
    const date = dividedDate[0];
    const { activeIndex, rejectReason, buyerInvestigationNotes } = this.state;
    const { reason } = this.state;
    const { errors } = this.props;
    return (
      <Card key={this.props.id} color='black'>
        {this.props.buyer &&
          this.props.decision === undefined &&
          this.props.requestStatus === "open" && (
            <>
              <Popup
                content='Edit Return Reason'
                basic
                trigger={
                  <Button circular icon='edit' onClick={this.handleClick} />
                }
              />
              <Accordion>
                <Accordion.Title
                  active={activeIndex === 0}
                  index={0}
                  onClick={this.handleClick}
                  style={{ marginTop: "-14%" }}
                >
                  <Icon />
                </Accordion.Title>
                <Accordion.Content active={activeIndex === 0}>
                  <Segment>
                    <Label style={{ margin: "5px" }}>
                      Edit your return reason
                    </Label>
                    <Form onSubmit={this.handleSubmit}>
                      <Form.TextArea
                        name='reason'
                        size='large'
                        value={
                          reason === " " && this.props.reason
                            ? this.props.reason
                            : reason
                        }
                        placeholder='Enter your new reason here..'
                        onChange={this.handleChange}
                        error={errors && errors.reason}
                      />
                      <Button
                        color='blue'
                        style={{ marginLeft: "43%", marginTop: "5%" }}
                        onClick={this.handleSubmit}
                      >
                        Submit Reason
                      </Button>
                    </Form>
                  </Segment>
                </Accordion.Content>
              </Accordion>
            </>
          )}
        <Card.Content>
          <Card.Header style={{ marginTop: "2%" }}>
            {this.props.title}{" "}
          </Card.Header>
          <Card.Meta style={{ marginBottom: "2%" }}>
            Request Id : <Label>{this.props.id}</Label>
            <br />
            Created At:{" "}
            <Label style={{ marginLeft: "2%", marginTop: "2%" }} color='blue'>
              {date}
            </Label>
          </Card.Meta>
          <Card.Meta>
            Request Status:{" "}
            {this.props.requestStatus === "open" ? (
              <Label style={{ marginLeft: "2%" }} color='green'>
                {this.props.requestStatus.toUpperCase()}
              </Label>
            ) : (
              <Label color='red'>
                {this.props.requestStatus.toUpperCase()}
              </Label>
            )}
          </Card.Meta>
          <Card.Description>
            {" "}
            <Label style={{ textAlign: "center" }}>Return Reason</Label>
            <Segment>{this.props.reason}</Segment>
          </Card.Description>
          <Card.Description style={{ marginTop: "5%" }}>
            Seller Decision:{" "}
            {this.props.decision === undefined ? (
               this.props.request.sellerData.showed?(
                <Label>No Decision Yet</Label>):(
                  <Label color='red'>You Haven't Taken Any Decision!</Label>)
            ) : this.props.decision === "accept" ? (
              <Label style={{ marginLeft: "3%" }} color='green'>
                Accepted
              </Label>
            ) : (
              this.props.decision === "rejectOnPickUp" ?(<Label style={{ marginLeft: "3%" }} color='red'>
              Won't Pick Up
            </Label>):(<Label style={{ marginLeft: "3%" }} color='red'>
              Rejected
            </Label>)
              
            )}{" "}
            <br />
            <br />
            {this.props.sellerReason ? (
              <div>
                Seller Reject Reason:{" "}
                <Segment>{this.props.sellerReason}</Segment>
              </div>
            ) : (
              ""
            )}
          </Card.Description>
          <Divider />
          Investigation Status:
          <Card.Description style={{ padding: "3%" }}>
            {this.props.investigation === "New" ? (
              <Label>No Investigation yet</Label>
            ) : this.props.investigation === "InProgress" ? (
              <Label color='yellow'>In Progress</Label>
            ) : (
              <>
                <Label color='purple'>Finished</Label>
                {this.props.outcome && (
                  <div style={{ paddingTop: "5%" }}>
                    {" "}
                    Investigation Outcome:
                    <Label style={{ marginTop: "3%", marginBottom: "3%" }}>
                      {this.props.outcome}
                    </Label>
                  
                  </div>
                )}
              </>
            )}
            <Divider />
            {this.props.buyerNotes && (
              <>
                Buyer Notes on Investigation:{" "}
                <Segment>{this.props.buyerNotes}</Segment>
                <Divider />
              </>
            )}
            Order: <Label>{this.props.orderID}</Label>
          </Card.Description>
          {this.props.buyer &&
            this.props.decision === undefined &&
            this.props.requestStatus !== "canceled" && (
              <div>
                {" "}
                <Divider />
                <Button
                  color='purple'
                  onClick={() => {
                    this.props.cancelReturnRequest(this.props.id);
                  }}
                >
                  {" "}
                  Cancel this Request
                </Button>
              </div>
            )}
          {!this.props.buyer && this.props.request.sellerData.showed &&
          this.props.requestStatus === "open" &&
          this.props.decision === undefined &&
          this.props.investigation === "New" ? (
            <Segment>
              Buyer has requested a return request{" "}
              <div>
                <Button
                  color='green'
                  onClick={(e) => {
                    this.props.sellerDecision("accept", this.props.id);
                  }}
                  value='accept'
                >
                  Accept
                </Button>
                <Button
                  color='red'
                  onClick={this.handleRejectRequest}
                  value='reject'
                >
                  Reject
                </Button>
              </div>{" "}
              <Label style={{ marginTop: "5%" }}>Rejection Reason</Label>
              <Form>
                <Form.TextArea
                  name='rejectReason'
                  type='string'
                  placeholder='type reason here ...'
                  onChange={this.handleChangeReason}
                  value={rejectReason}
                  error={errors && errors.rejectReason}
                />
              </Form>
            </Segment>
          ) : (
            ""
          )}
          {this.props.buyer &&
            this.props.decision === "reject" &&
            this.props.investigation === "New" && (
              <Segment>
                Would you like to open an <strong>Investigation</strong>
                ? <br />
                <br />
                <Button
                  color='green'
                  onClick={(e) => {
                    this.handleBuyerInvestigationDecision("request");
                  }}
                >
                  Yes
                </Button>
                <Button
                  color='red'
                  onClick={(e) => {
                    this.handleBuyerInvestigationDecision("ignore");
                  }}
                >
                  No
                </Button>
                <Divider />
                <Label style={{ marginTop: "5%" }}>Investigation Notes</Label>
                <Form>
                  <Form.TextArea
                    name='buyerInvestigationNotes'
                    type='string'
                    placeholder='Notes ...'
                    onChange={this.handleChangeNotes}
                    value={buyerInvestigationNotes}
                    error={errors && errors.buyerInvestigationNotes}
                  />
                </Form>
              </Segment>
            )}
        </Card.Content>
        <Card.Content extra>
          {
            this.props.buyer?(
              this.props.request.sellerData.decision==='accept' && this.props.request.buyerData.showed?(
              this.props.request.buyerData.SP && this.props.request.buyerData.timeSlot?(
                this.props.request.dropOffTime?( <div>
                  <Label color='yellow' fluid size='large'>Item Dropped Off Details:</Label>
                <Label >SP: {this.props.request.buyerData.SP.name} </Label>
                <br/>
                <Label>Timeslot:{this.formatDate(this.props.request.dropOffTime)} </Label>
                </div>
                ):( <div>
                  <Label color='yellow' fluid size='large'> Chosen Drop off Details</Label>
                <Label >SP: {this.props.request.buyerData.SP.name} </Label>
                <br/>
                <Label>Timeslot:{this.formatDate(this.props.request.buyerData.timeSlot)} </Label>
                </div>)
               
              ):(
                <Button color='green' as={Link} to={`/returnedItemDropOffInfo/${this.props.request._id}`}>Choose Returned Item Drop Off Info</Button>
              )):(this.props.request.sellerData.decision==='accept'&& <Label color='red'>You Did Not Show Return Request Is Automatically Closed</Label>)
            ):(
              this.props.request.sellerData.showed?(
              this.props.request.sellerData.SP && this.props.request.sellerData.timeSlot?(
                this.props.request.pickUpTime?(  <div>
                  <Label color='yellow' fluid size='large'>Item Pick Up Details:</Label>
                  <br/>
                  <Label >SP: {this.props.request.sellerData.SP.name} </Label>
                  <br/>
                  <Label>Timeslot:{this.formatDate(this.props.request.pickUpTime)} </Label>
                  </div>):(  
                  this.props.request.sellerData.decision==='rejectOnPickUp'?(
                  this.props.request.investigation.outcome === 'Seller Fault'?( <div>
                    <Label color='red' fluid size='large'>It is your fault. Collect your Item from the SP!</Label>
                    </div>):
                    (<Label color='red' fluid size='large'>You Rejected To Pick Up Item!</Label>
                    )):( <div>
                  <Label color='yellow' fluid size='large'>Chosen Pick Up Details</Label>
                  <br/>
                  <Label >SP: {this.props.request.sellerData.SP.name} </Label>
                  <br/>
                  <Label>Timeslot:{this.formatDate(this.props.request.sellerData.timeSlot)} </Label>
                  </div>)
                 )
              
              ):(
                this.props.request.sellerData.decision==='accept' && this.props.request.buyerData.SP && this.props.request.buyerData.timeSlot && <Button color='green' as={Link} to={`/returnedItemPickUpInfo/${this.props.request._id}`}>Choose Returned Item Pick Up Info</Button>
                )):(
                  <Label  color='red'>You Did Not Show </Label>
                )
            )
          }
          
        </Card.Content>
      <Card.Content extra>
        {this.props.request.sellerData.decision === 'reject' && this.props.request.investigation.status === 'Finished'?(        
          this.props.buyer?(
            this.props.request.investigation.outcome?(
              this.props.request.investigation.outcome === 'Buyer Fault' ?(
                  <Label color='red'>This is your fault and the return request is discarded!</Label>
            ):(
            <div>
            {!this.props.request.dropOffTime?( 
            <React.Fragment>
            <Label color='red'>This is the seller's fault kindly drop off the item at sp for your money to be transfered back to you.</Label>
            <br/>
            {this.props.request.buyerData.SP && this.props.request.buyerData.timeSlot?(<div>
                  <Label color='yellow' fluid size='large'> Chosen Drop off Details</Label>
                <Label >SP: {this.props.request.buyerData.SP.name} </Label>
                <br/>
                <Label>Timeslot:{this.formatDate(this.props.request.buyerData.timeSlot)} </Label>
                </div>):
            (<Button color='green' as={Link} to={`/returnedItemDropOffInfo/${this.props.request._id}`}>Choose Returned Item Drop Off Info</Button>)}
            </React.Fragment>
            )
            :(
              <React.Fragment>
              <Label color='green'>You have successfully dropped off the item.</Label>
            <Label>This is the seller's fault and the money is transfered back to you!</Label>
            </React.Fragment>)}
            </div>
            )
          ):( <Label>Case Closed!</Label>)
        ):(
           this.props.request.investigation.outcome?(
            this.props.request.investigation.outcome === 'Buyer Fault' ?(
