import React, { Component } from "react";
import { Button, Container, Segment } from "semantic-ui-react";
import { withTranslation } from "react-i18next";
import i18n from "i18next";
import { Link } from "react-router-dom";
import Footer from "../LandingPage/Footer";
import { NavBar } from "./tremsConditionsNavbar";
import "./style.css";
import { history } from "../../helpers";
import { routeConstants } from "../../constants";
class TermsandConditionsPage extends Component {
  componentDidMount() {
    document.body.dir = i18n.dir();
  }
  handleBackButton() {
    history.push(`${routeConstants.LANDINGPAGE}`); 
  }
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    return (
      <Container fluid>
        <NavBar
          title={t("landingPage:termsConditionsnavbarTitle")}
          subtitle={t("landingPage:termsConditionsnavbarSubTitle")}
        />
        <Segment className={`termsAndConditions ${language}`}>
          <div>
            <Link to="/legal/terms/FeesPolicy">
              <p>
                {language !== "ar" ? (
                  <p>Fees Policy</p>
                ) : (
                  <p> سياسات الرسوم </p>
                )}
              </p>
            </Link>

            <Link to="/legal/terms/TermsAndConditions">
              <p>
                {language !== "ar" ? (
                  <p>User Agreement</p>
                ) : (
                  <p>شروط الاستخدام</p>
                )}
              </p>
            </Link>
            <Link to="/legal/terms/productPolicy">
              <p>
                {language !== "ar" ? (
                  <p>Prohibited and Restricted Products Policy</p>
                ) : (
                  <p>سياسات المنتجات</p>
                )}
              </p>
            </Link>
          </div>
          <Button className={`backButton ${language}`} onClick={this.handleBackButton}>
            {t("landingPage:TermsAndConditionsButton")}
          </Button>
        </Segment>
        <Footer />
      </Container>
    );
  }
}
export default withTranslation("landingPage")(TermsandConditionsPage);
