import { voucherConstants } from '../constants';
import { voucherService } from './../services';

export const voucherActions = {
    validateVoucherCode,
    clearVoucherState,
}

function clearVoucherState() {
    return (dispatch) => {
        dispatch({ type: voucherConstants.CLEAR });
    };
}

function validateVoucherCode(code) {
    return async dispatch => {
        await dispatch(request());
        try {
            const { data } = await voucherService.validateVoucherCode(code);
            dispatch(success(data));
        } catch (ex) {
            dispatch(failure(ex));
        }
    };

    function request() {
        return { type: voucherConstants.VALIDATE_VOUCHER_CODE_REQUEST }
    }

    function success({ voucher }) {
        return { type: voucherConstants.VALIDATE_VOUCHER_CODE_SUCCESS, voucher }
    }

    function failure(error) {
        return { type: voucherConstants.VALIDATE_VOUCHER_CODE_FAILURE, error }
    }
}
