import { httpService } from './';
import { apiUrl } from '../config';


export const returnRequestsService = {
    viewAllReturnRequests,
    sellerDecision,
    openInvestigation,
    cancelReturnRequest,
    editReturnRequest,
    confirmReturnRequestDropOffInfo,
    viewReturnRequest,
    confirmReturnRequestPickUpInfo
};



const apiEndpoint = apiUrl + '/returnRequest';




function sellerDecision(decision,requestId,body){
    return httpService.put(apiEndpoint+`/seller/${decision}/${requestId}`,body)
    }

function viewAllReturnRequests() {
    return httpService.get(apiEndpoint);
}


function openInvestigation(decision,requestId,body){
    return httpService.put(apiEndpoint+`/investigation/${requestId}/${decision}`,body)

}

function cancelReturnRequest(requestId){
    return httpService.delete(apiEndpoint+`/${requestId}`)
}

function editReturnRequest(requestId,body){
return httpService.put(apiEndpoint+`/${requestId}`,body)

}
function confirmReturnRequestDropOffInfo(requestId,returnReqDropOffInfo){
    return httpService.put(apiEndpoint+`/dropOffInfo/${requestId}`,returnReqDropOffInfo)
    
}
function viewReturnRequest(requestId){
    return httpService.get(apiEndpoint+`/${requestId}`);
    
}
function confirmReturnRequestPickUpInfo(requestId,returnReqPickUpInfo){
    return httpService.put(apiEndpoint+`/pickUpInfo/${requestId}`,returnReqPickUpInfo)
}
